import { FlatList, StyleSheet, View } from 'react-native';
import { useEffect, useState } from 'react';

import { Provider as PaperProvider, TextInput, Paragraph } from 'react-native-paper';
import { Button, Card } from 'react-native-paper';

import { StatusBar } from 'expo-status-bar';

import { SafeAreaView } from 'react-native-safe-area-context';

import { initializeApp } from "firebase/app";
import { getAuth, signInWithCustomToken } from "firebase/auth";
import { getDatabase, ref, push, serverTimestamp, query, orderByChild, equalTo, limitToLast } from "firebase/database";
import { getFunctions, httpsCallable } from 'firebase/functions';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useList } from 'react-firebase-hooks/database';

const firebaseConfig = {
  apiKey: "AIzaSyDBjUEw_DQNMQsZJWfTtLL0PQJoH-xF0kk",
  authDomain: "sta-cs5041.firebaseapp.com",
  databaseURL: "https://sta-cs5041-p4.firebaseio.com",
  projectId: "sta-cs5041",
  storageBucket: "sta-cs5041.appspot.com",
  messagingSenderId: "639987847762",
  appId: "1:639987847762:web:c5a35616a1aa1cf243458b"
};

const firebaseToken = "9dd8d43a-01db-4286-b0e8-371d24237380";

const firebaseApp = initializeApp(firebaseConfig);
const auth = getAuth(firebaseApp);
const database = getDatabase(firebaseApp);
const functions = getFunctions(firebaseApp);

const CardComponent = ({ message }) => (
  <Card style={styles.card}>
    <Card.Content>
      <Paragraph>{message}</Paragraph>
    </Card.Content>
  </Card>
);

const Add = ({ user }) => {
  const [text, setText] = useState("");

  return (
    <View style={styles.inputContainer}>
      <TextInput
        style={styles.textInput}
        label="Message"
        value={text}
        onChangeText={text => setText(text)}
      />
      <Button
        style={styles.sendButton}
        icon="send"
        mode="contained"
        onPress={() => {
          push(ref(database, "data"), {
            userId: user.uid,
            groupId: 20,
            timestamp: serverTimestamp(),
            type: "str",
            string: text.toString()
          });
        }}
      >
        post
      </Button>
    </View>
  );
};

const Messages = ({ messages }) => {
  return (
    <FlatList
      style={styles.messageList}
      data={messages}
      renderItem={({ item }) => <CardComponent message={item} />}
      keyExtractor={(item, index) => index.toString()}
    />
  );
};

export default function MessagesScreen() {
  const [user, authLoading, authError] = useAuthState(auth);

  useEffect(() => {
    (async () => {
      const getToken = httpsCallable(functions, "getToken");
      const token = await getToken({ token: firebaseToken });
      if (token?.data?.result === "ok" && token?.data?.token) {
        signInWithCustomToken(auth, token.data.token);
      } else {
        console.error(token?.data?.reason ?? "unknownError")
      }
    })();
  }, []);

  const [snapshots, dbLoading, dbError] = useList(user ? query(ref(database, 'data'), orderByChild('groupId'), equalTo(20), limitToLast(3)) : null);

  return (
  <PaperProvider>
  <SafeAreaView style={styles.container}>
  <Add user={user} />
  {snapshots ? (
  <Messages messages={snapshots.map(el => el?.val()?.string ?? '')} />
  ) : null}
  <StatusBar style="auto" />
  </SafeAreaView>
  </PaperProvider>
  );
  }

  const styles = StyleSheet.create({
  container: {
  flex: 1,
  backgroundColor: '#fff',
  },
  inputContainer: {
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  padding: 10,
  },
  textInput: {
  flex: 1,
  marginRight: 10,
  },
  sendButton: {
  alignSelf: 'center',
  },
  messageList: {
  paddingHorizontal: 10,
  },
  card: {
  marginTop: 10,
  marginBottom: 10,
  },
  });
