import React from 'react';
import { View, Text, StyleSheet, Dimensions } from 'react-native';

const emotions = [
  { name: 'Happy', color: '#F9D342' },
  { name: 'Sad', color: '#4A90E2' },
  { name: 'Angry', color: '#FF6B6B' },
  { name: 'Excited', color: '#1DE9B6' },
  { name: 'Calm', color: '#9ED8F6' },
  { name: 'Anxious', color: '#FFB200' },
  { name: 'Bored', color: '#AA96DA' },
  { name: 'Surprised', color: '#FFA87A' },
  { name: 'Neutral', color: '#D0D1D4' },
];

const deviceWidth = Dimensions.get('window').width;
const deviceHeight = Dimensions.get('window').height;
const cardSize = deviceWidth / 4; // Reduce the card size
const cardMargin = (deviceWidth * 0.01) / 2; // Reduce the card margin

const MoodScreen = () => {
  const numEmotions = emotions.length;
  const numRows = Math.ceil(numEmotions / 3);
  const totalCardsHeight = cardSize * numRows + cardMargin * (numRows - 1);
  const extraSpace = deviceHeight * 0.8 - totalCardsHeight;
  const spaceBetweenCards = extraSpace / (numRows - 1);
  const emptyCards = Array(3 - (numEmotions % 3)).fill(null);

  return (
    <View style={styles.container}>
      <View style={styles.emotionCardsContainer}>
        {emotions.map((emotion, index) => (
          <View
            key={index}
            style={[
              styles.emotionCard,
              { backgroundColor: emotion.color, marginBottom: spaceBetweenCards },
              index % 3 === 2 && { marginRight: 0 }, // Remove margin right for last card in each row
            ]}
          >
            <Text style={styles.emotionText}>{emotion.name}</Text>
          </View>
        ))}
        {emptyCards.map((_, index) => (
          <View key={index} style={styles.emotionCard} />
        ))}
      </View>
    </View>
  );
};

export default MoodScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },

  emotionCardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
  },

  emotionCard: {
    width: cardSize,
    height: cardSize,
    justifyContent: 'center',
    alignItems: 'center',
    margin: cardMargin / 2,
    borderRadius: 10,
  },

  emotionText: {
    fontSize: 14,
    fontWeight: 'bold',
    color: 'white',
  },
});
