import {
    ScrollView,
    StyleSheet,
    View,
    SafeAreaView,
    FlatList,
    Text,
    Paragraph,
  } from 'react-native';
  import { useEffect, useState } from 'react';

  import {
    Provider as PaperProvider,
    TextInput,
    Button as PaperButton,
    Card as PaperCard, // 使用别名
    Paragraph as PaperParagraph, // 使用别名，避免与 react-native 中的 Paragraph 冲突
  } from 'react-native-paper';

  import { StatusBar } from 'expo-status-bar';

  import { initializeApp } from "firebase/app";
  import { getAuth, signInWithCustomToken } from "firebase/auth";
  import { getDatabase, ref, push, serverTimestamp, query, orderByChild, equalTo, limitToLast } from "firebase/database";
  import { getFunctions, httpsCallable } from 'firebase/functions';

  import { useAuthState } from 'react-firebase-hooks/auth';
  import { useList } from 'react-firebase-hooks/database';

  import { ListItem, Input, Button } from 'react-native-elements';

  const firebaseConfig = {
    apiKey: "AIzaSyDBjUEw_DQNMQsZJWfTtLL0PQJoH-xF0kk",
    authDomain: "sta-cs5041.firebaseapp.com",
    databaseURL: "https://sta-cs5041-p4.firebaseio.com",
    projectId: "sta-cs5041",
    storageBucket: "sta-cs5041.appspot.com",
    messagingSenderId: "639987847762",
    appId: "1:639987847762:web:c5a35616a1aa1cf243458b"
  };

  const firebaseToken = "9dd8d43a-01db-4286-b0e8-371d24237380";

  const firebaseApp = initializeApp(firebaseConfig);
  const auth = getAuth(firebaseApp);
  const database = getDatabase(firebaseApp);
  const functions = getFunctions(firebaseApp);

  const TemperatureCard = ({ message }) => (
    <PaperCard style={styles.card}>
      <PaperCard.Content>
        <PaperParagraph style={styles.cardNumber}>
          {message.type === 'int' ? `${message.integer}°C` : 'unknown'}
        </PaperParagraph>
      </PaperCard.Content>
    </PaperCard>
  );


  const Add = ({ user }) => {
    const [text, setText] = useState('');

    return (
      <View style={styles.inputContainer}>
        <Input
          containerStyle={styles.textInput}
          placeholder="Message"
          value={text}
          onChangeText={(text) => setText(text)}
        />
        <Button
        icon={{ name: 'send', color: 'white' }}
        buttonStyle={styles.sendButton}
        onPress={() => {
        push(ref(database, 'data'), {
            userId: user.uid,
            groupId: 1,
            timestamp: serverTimestamp(),
            type: 'int',
            integer: parseInt(text),
        });
    }}
    title="Post"
/>
      </View>
    );
  };

  const renderItem = ({ item }) => (
    <ListItem bottomDivider>
      <ListItem.Content>
        <ListItem.Title>{item.string}</ListItem.Title>
      </ListItem.Content>
    </ListItem>
  );

  const Messages = ({ outsideMessages, insideMessages }) => {
    return (
      <>
        <Text style={styles.title}>Outside Temperature</Text>
        <FlatList
          style={styles.messageList}
          data={outsideMessages}
          renderItem={({ item }) => <TemperatureCard message={item.val()} />}
          keyExtractor={(item, index) => index.toString()}
        />
        <Text style={styles.title}>Inside Temperature</Text>
        <FlatList
          style={styles.messageList}
          data={insideMessages}
          renderItem={({ item }) => <TemperatureCard message={item.val()} />}
          keyExtractor={(item, index) => index.toString()}
        />
      </>
    );
  };



  export default function MessagesScreen() {
    const [user, authLoading, authError] = useAuthState(auth);

    useEffect(() => {
      (async () => {
        const getToken = httpsCallable(functions, 'getToken');
        const token = await getToken({ token: firebaseToken });
        if (token?.data?.result === 'ok' && token?.data?.token) {
          signInWithCustomToken(auth, token.data.token);
        } else {
          console.error(token?.data?.reason ?? 'unknownError');
        }
      })();
    }, []);

    //外部温度
    const [snapshots, dbLoading, dbError] = useList(
      user
        ? query(
            ref(database, 'data'),
            orderByChild('groupId'),
            equalTo(1),
            limitToLast(1)
          )
        : null
    );

    //内部温度
    const [insideSnapshots, insideDbLoading, insideDbError] = useList(
        user
          ? query(
              ref(database, 'data'),
              orderByChild('groupId'),
              equalTo(2),
              limitToLast(1)
            )
          : null
      );


      return (
        <PaperProvider>
          <SafeAreaView style={styles.container}>
            {snapshots && insideSnapshots ? (
              <Messages outsideMessages={snapshots} insideMessages={insideSnapshots} />
            ) : null}
            <StatusBar style="auto" />
          </SafeAreaView>
        </PaperProvider>
      );

  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    card: {
      marginLeft: 10,
      marginRight: 10,
      marginTop: 10,
      marginBottom: 10,
    },
    inputContainer: {
      display: 'flex',
      flexDirection: 'row',
    },
    textInput: {
      flex: 1,
    },
    sendButton: {
      backgroundColor: 'blue',
      margin: 10,
    },
    messageList: {
      margin: 10,
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        textAlign: 'center',
        marginTop: 10,
        marginBottom: 10,
    },
    cardNumber: {
        fontSize: 36,
        fontWeight: 'bold',
        color: '#2196F3',
        textAlign: 'center',
    },
  });
