import React, { useState } from 'react';
import { View, Text, StyleSheet, Dimensions, TouchableOpacity, Image } from 'react-native';

const emotions = [
  { name: 'Happy', color: '#F9D342', icon: require('asserts/happy.png') },
  { name: 'Sad', color: '#4A90E2', icon: require('asserts/sad.png') },
  { name: 'Angry', color: '#FF6B6B', icon: require('asserts/angry.png') },
  { name: 'Excited', color: '#1DE9B6', icon: require('asserts/excited.png') },
  { name: 'Calm', color: '#9ED8F6', icon: require('asserts/calm.png') },
  { name: 'Anxious', color: '#FFB200', icon: require('asserts/anxious.png') },
  { name: 'Bored', color: '#AA96DA', icon: require('./path/to/bored-icon') },
  { name: 'Surprised', color: '#FFA87A', icon: require('./path/to/surprised-icon') },
  { name: 'Neutral', color: '#D0D1D4', icon: require('./path/to/neutral-icon') },
];

const deviceWidth = Dimensions.get('window').width;
const deviceHeight = Dimensions.get('window').height;
const cardSize = deviceWidth / 4; // Reduce the card size
const cardMargin = (deviceWidth * 0.01) / 2; // Reduce the card margin

const MoodScreen = () => {
  const [selectedEmotion, setSelectedEmotion] = useState(null);

  const numEmotions = emotions.length;
  const numRows = Math.ceil(numEmotions / 3);
  const emptyCards = Array(3 - (numEmotions % 3)).fill(null);

  return (
    <View style={styles.container}>
      <View style={styles.emotionCardsContainer}>
        {emotions.map((emotion, index) => (
          <TouchableOpacity key={index} onPress={() => setSelectedEmotion(emotion)}>
            <View
              style={[
                styles.emotionCard,
                { backgroundColor: emotion.color },
              ]}
            >
              <Text style={styles.emotionText}>{emotion.name}</Text>
            </View>
          </TouchableOpacity>
        ))}
        {emptyCards.map((_, index) => (
          <View key={index} style={styles.emotionCard} />
        ))}
      </View>
      <View style={{ flex: 1 }}>
        {selectedEmotion && (
          <Image
            source={selectedEmotion.icon}
            style={{ width: 24, height: 24 }}
          />
        )}
      </View>
    </View>
  );
};

export default MoodScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },

  emotionCardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    width: '100%',
    height: deviceHeight * 0.8,
    alignItems: 'center',
  },

  emotionCard: {
    width: cardSize,
    height: cardSize,
    justifyContent: 'center',
    alignItems: 'center',
    margin: cardMargin,
    borderRadius: 10,
  },

  emotionText: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
  },
});
