import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  TouchableOpacity,
  Modal,
  TextInput,
  Button,
} from 'react-native';
import { getDatabase, ref, push, serverTimestamp } from 'firebase/database';

const emotions = [
  // ...
];

const deviceWidth = Dimensions.get('window').width;
const deviceHeight = Dimensions.get('window').height;
const cardSize = deviceWidth / 4;
const cardMargin = (deviceWidth * 0.01) / 2;

const MoodScreen = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [inputText, setInputText] = useState('');

  const handleCardPress = () => {
    setModalVisible(true);
  };

  const handleSubmit = () => {
    const database = getDatabase();
    push(ref(database, 'data'), {
      // Replace `userId` with the appropriate value
      userId: 'userId_placeholder',
      groupId: 30,
      timestamp: serverTimestamp(),
      type: 'str',
      string: inputText,
    });

    setModalVisible(false);
    setInputText('');
  };

  // ...

  return (
    <View style={styles.container}>
      <View style={styles.emotionCardsContainer}>
        {emotions.map((emotion, index) => (
          <TouchableOpacity
            key={index}
            onPress={handleCardPress}
            style={[styles.emotionCard, { backgroundColor: emotion.color }]}
          >
            <Text style={styles.emotionText}>{emotion.name}</Text>
          </TouchableOpacity>
        ))}
      </View>
      <View style={{ flex: 1 }} />
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <TextInput
              style={styles.input}
              onChangeText={(text) => setInputText(text)}
              value={inputText}
              placeholder="Enter your message"
            />
            <Button onPress={handleSubmit} title="Submit" color="#2196F3" />
          </View>
        </View>
      </Modal>
    </View>
  );
};

export default MoodScreen;

const styles = StyleSheet.create({
  // ...
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalView: {
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    width: '100%',
    marginBottom: 15,
  },
});
