import { createDrawerNavigator } from '@react-navigation/drawer';
import { NavigationContainer } from '@react-navigation/native';
import { Button, View, StyleSheet } from 'react-native';
import { TouchableOpacity, Text } from "react-native";
import { Animated } from 'react-native';


import { Video } from "expo-av";
import { useMemo } from "react";

import MessageScreen from './MessageScreen.js';
import WeatherScreen from './WeatherScreen.js';
import MoodScreen from './MoodScreen.js';
import React, { useState } from 'react';

function HomeScreen({ navigation }) {
  const [opacity] = useState(new Animated.Value(0));
  return (
    <View style={styles.container}>
      <View style={styles.background}>
        <Animated.View
          style={[styles.backgroundViewWrapper, { opacity: opacity }]}
        >
          <Video
            isLooping
            isMuted
            positionMillis={500}
            onLoad={() => {
              // https://facebook.github.io/react-native/docs/animated#timing
              Animated.timing(opacity, {
                toValue: 1,
                useNativeDriver: f,
              }).start();
            }}
            resizeMode="cover"
            shouldPlay
            source={{
              uri: "http://d23dyxeqlo5psv.cloudfront.net/big_buck_bunny.mp4",
            }}
            style={{ flex: 1 }}
          />
        </Animated.View>
      </View>
      <TouchableOpacity
        style={styles.button}
        onPress={navigation.openDrawer}
      >
        <Text style={styles.buttonText}>Open navigation drawer</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate("MessageScreen")}
      >
        <Text style={styles.buttonText}>Go to Message to Fife Rabbits</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate("WeatherScreen")}
      >
        <Text style={styles.buttonText}>Go to check the Fife environment</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate("MoodScreen")}
      >
        <Text style={styles.buttonText}>Go to send you color of moods</Text>
      </TouchableOpacity>
    </View>
  );
}



const Drawer = createDrawerNavigator();

function App() {
  return (
    <NavigationContainer>
      <Drawer.Navigator initialRouteName="Home">
        <Drawer.Screen name="Home" component={HomeScreen} />
        <Drawer.Screen name="MessageScreen" component={MessageScreen} />
        <Drawer.Screen name="WeatherScreen" component={WeatherScreen} />
        <Drawer.Screen name="MoodScreen" component={MoodScreen} />
      </Drawer.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#f5f5f5", // 设置背景色
  },
  button: {
    width: "100%",
    maxWidth: 300,
    backgroundColor: "#1e90ff", // 设置按钮背景色
    padding: 15,
    borderRadius: 8, // 设置按钮圆角
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 10, // 设置按钮之间的间距
  },
  buttonText: {
    color: "white", // 设置按钮文本颜色
    fontWeight: "bold", // 设置按钮文本加粗
    fontSize: 16, // 设置按钮文本大小
  },
});



export default App;
