import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import { Input, Button } from 'react-native-elements';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const MuseumStoryForm = () => {
  const [alias, setAlias] = useState('');
  const [location, setLocation] = useState('');
  const [time, setTime] = useState('');
  const [recipient, setRecipient] = useState('');
  const [story, setStory] = useState('');

  const handleSubmit = () => {
    // 在这里处理提交表单的逻辑
    console.log({
      alias,
      location,
      time,
      recipient,
      story,
    });
  };

  return (
    <View style={styles.container}>
      <Input
        label="Your Name"
        value={alias}
        onChangeText={text => setAlias(text)}
        style={styles.input}
        leftIcon={<MaterialCommunityIcons name="account" size={24} color="black" />}
      />
      <Input
        label=""
        value={location}
        onChangeText={text => setLocation(text)}
        style={styles.input}
        leftIcon={<MaterialCommunityIcons name="map-marker" size={24} color="black" />}
      />
      <Input
        label="时间"
        value={time}
        onChangeText={text => setTime(text)}
        style={styles.input}
        leftIcon={<MaterialCommunityIcons name="calendar" size={24} color="black" />}
      />
      <Input
        label="你想传达的人"
        value={recipient}
        onChangeText={text => setRecipient(text)}
        style={styles.input}
        leftIcon={<MaterialCommunityIcons name="account-multiple" size={24} color="black" />}
      />
      <Input
        label="你的故事"
        value={story}
        onChangeText={text => setStory(text)}
        multiline={true}
        numberOfLines={4}
        style={styles.input}
        leftIcon={<MaterialCommunityIcons name="book" size={24} color="black" />}
      />
      <Button title="提交" onPress={handleSubmit} containerStyle={styles.buttonContainer} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 20,
    backgroundColor: '#fff',
  },
  input: {
    marginBottom: 10,
  },
  buttonContainer: {
    marginTop: 10,
  },
});

export default MuseumStoryForm;
