import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { Card } from 'react-native-elements';
import { getDatabase, ref, query, orderByChild, equalTo, limitToLast, onValue } from 'firebase/database';
import { useAuthState } from 'react-firebase-hooks/auth';
import { useList } from 'react-firebase-hooks/database';


const firebaseConfig = {
    apiKey: "AIzaSyDBjUEw_DQNMQsZJWfTtLL0PQJoH-xF0kk",
    authDomain: "sta-cs5041.firebaseapp.com",
    databaseURL: "https://sta-cs5041-p4.firebaseio.com",
    projectId: "sta-cs5041",
    storageBucket: "sta-cs5041.appspot.com",
    messagingSenderId: "639987847762",
    appId: "1:639987847762:web:c5a35616a1aa1cf243458b"
  };

  const firebaseToken = "9dd8d43a-01db-4286-b0e8-371d24237380";

const MuseumStoryCards = () => {
  const [user, authLoading, authError] = useAuthState(auth);
  const [stories, setStories] = useState([]);

  useEffect(() => {
    if (user) {
      const db = getDatabase();
      const aliasQuery = query(ref(db, 'data'), orderByChild('groupId'), equalTo(30));
      const locationQuery = query(ref(db, 'data'), orderByChild('groupId'), equalTo(31));
      const timeQuery = query(ref(db, 'data'), orderByChild('groupId'), equalTo(32));
      const recipientQuery = query(ref(db, 'data'), orderByChild('groupId'), equalTo(33));
      const storyQuery = query(ref(db, 'data'), orderByChild('groupId'), equalTo(34));

      const [aliasSnapshot] = useList(aliasQuery);
      const [locationSnapshot] = useList(locationQuery);
      const [timeSnapshot] = useList(timeQuery);
      const [recipientSnapshot] = useList(recipientQuery);
      const [storySnapshot] = useList(storyQuery);

      const formattedStories = formatStories(
        aliasSnapshot,
        locationSnapshot,
        timeSnapshot,
        recipientSnapshot,
        storySnapshot
      );
      setStories(formattedStories);
    }
  }, [user]);

  const formatStories = (alias, location, time, recipient, story) => {
    const formattedStories = [];
    if (!alias || !location || !time || !recipient || !story) return formattedStories;

    for (let i = 0; i < alias.length; i++) {
      formattedStories.push({
        alias: alias[i].val().string,
        location: location[i].val().string,
        time: time[i].val().string,
        recipient: recipient[i].val().string,
        story: story[i].val().string,
      });
    }

    return formattedStories;
  };

  return (
    <ScrollView>
      {stories.map((story, index) => (
        <Card key={index}>
          <Card.Title>{story.alias}</Card.Title>
          <Card.Divider />
          <View>
            <Text style={styles.text}>Location: {story.location}</Text>
            <Text style={styles.text}>Time: {story.time}</Text>
            <Text style={styles.text}>Recipient: {story.recipient}</Text>
            <Text style={styles.text}>Story: {story.story}</Text>
          </View>
        </Card>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  text: {
    marginBottom: 10,
  },
});

export default MuseumStoryCards;
