import React from 'react';
import { View, Text, StyleSheet, Dimensions } from 'react-native';

const emotions = [
  { name: 'Happy', color: '#F9D342' },
  { name: 'Sad', color: '#4A90E2' },
  { name: 'Angry', color: '#FF6B6B' },
  { name: 'Excited', color: '#1DE9B6' },
  { name: 'Calm', color: '#9ED8F6' },
  { name: 'Anxious', color: '#FFB200' },
  { name: 'Bored', color: '#AA96DA' },
  { name: 'Surprised', color: '#FFA87A' },
  { name: 'Neutral', color: '#D0D1D4' },
];

const deviceWidth = Dimensions.get('window').width;
const cardWidth = deviceWidth * 0.3;
const cardMargin = deviceWidth * 0.0166;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: 40, // Adjust this value to add more space at the top
  },

  emotionCardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    width: '100%',
  },

  emotionCard: {
    width: cardWidth,
    height: cardWidth,
    justifyContent: 'center',
    alignItems: 'center',
    margin: cardMargin,
    borderRadius: 10,
  },

  emotionText: {
    fontSize: 14,
    fontWeight: 'bold',
    color: 'white',
  },
});
