import {
    ScrollView,
    StyleSheet,
    View,
    SafeAreaView,
    FlatList,
    Text,
  } from 'react-native';
  import { useEffect } from 'react';

  import {
    Provider as PaperProvider,
    Card,
    Paragraph,
  } from 'react-native-paper';

  import { useAuthState } from 'react-firebase-hooks/auth';
  import { useList } from 'react-firebase-hooks/database';

  import {
    auth,
    database,
    query,
    orderByChild,
    equalTo,
    limitToLast,
    ref,
  } from './MessagesScreen'; // Import these from MessagesScreen.js

  const CardComponent = ({ message }) => (
    <Card style={styles.card}>
      <Card.Content>
        <Paragraph>{message}</Paragraph>
      </Card.Content>
    </Card>
  );

  const renderItem = ({ item }) => (
    <CardComponent message={`Value: ${item.val().int}`} />
  );

  export default function NewScreen() {
    const [user] = useAuthState(auth);

    const [snapshots] = useList(
      user
        ? query(
            ref(database, 'data'),
            orderByChild('groupId/type'),
            equalTo('1/int'),
            limitToLast(10)
          )
        : null
    );

    return (
      <PaperProvider>
        <SafeAreaView style={styles.container}>
          {snapshots ? (
            <FlatList
              data={snapshots}
              renderItem={renderItem}
              keyExtractor={(item, index) => index.toString()}
            />
          ) : (
            <Text>No data available</Text>
          )}
        </SafeAreaView>
      </PaperProvider>
    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    card: {
      marginLeft: 10,
      marginRight: 10,
      marginTop: 10,
      marginBottom: 10,
    },
  });
