const Add = ({ user }) => {
  const [text, setText] = useState('');

  return (
    <View style={styles.inputContainer}>
      <Input
        containerStyle={styles.textInput}
        placeholder="Message"
        value={text}
        onChangeText={(text) => setText(text)}
      />
      <Button
        icon={{ name: 'send', color: 'white' }}
        buttonStyle={styles.sendButton}
        onPress={() => {
          push(ref(database, 'data'), {
            userId: user.uid,
            groupId: 20,
            timestamp: serverTimestamp(),
            type: 'str',
            string: text.toString(),
          });
        }}
        title="Post"
      />
    </View>
  );
};


const emotions = [
  { name: 'Happy', color: '#F9D342' },
  { name: 'Sad', color: '#4A90E2' },
  { name: 'Angry', color: '#FF6B6B' },
  { name: 'Excited', color: '#1DE9B6' },
  { name: 'Calm', color: '#9ED8F6' },
  { name: 'Anxious', color: '#FFB200' },
  { name: 'Bored', color: '#AA96DA' },
  { name: 'Surprised', color: '#FFA87A' },
  { name: 'Neutral', color: '#D0D1D4' },
];

const deviceWidth = Dimensions.get('window').width;
const deviceHeight = Dimensions.get('window').height;
const cardSize = deviceWidth / 4; // Reduce the card size
const cardMargin = (deviceWidth * 0.01) / 2; // Reduce the card margin

const MoodScreen = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [inputText, setInputText] = useState('');
  const [snapshots, setSnapshots] = useState([]);

  const handleCardPress = () => {
    setModalVisible(true);
  };

  const handleSubmit = async () => {
    const user = auth.currentUser;

    try {
      await push(ref(database, 'data'), {
        userId: user.uid,
        groupId: 20,
        timestamp: serverTimestamp(),
        type: 'str',
        string: inputText.toString(),
      });
    } catch (error) {
      console.log('Error posting message:', error);
    }

    setModalVisible(false);
    setInputText('');
  };

  useEffect(() => {
    //const messagesRef = ref(database, 'data');
    //const messagesQuery = messagesRef.orderByChild('groupId').equalTo(20);

    const handleSnapshot = (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        const messages = Object.keys(data).map((key) => ({
          id: key,
          ...data[key],
        }));
        setSnapshots(messages);
      }
    };

    onValue(messagesQuery, handleSnapshot);

    return () => {
      off(messagesQuery, handleSnapshot);
    };
  }, []);

  const numEmotions = emotions.length;
  const numRows = Math.ceil(numEmotions / 3);
  const emptyCards = Array(3 - (numEmotions % 3)).fill(null);

  return (
    <View style={styles.container}>
      <View style={styles.emotionCardsContainer}>
        {emotions.map((emotion, index) => (
          <TouchableOpacity
            key={index}
            onPress={handleCardPress}
            style={[styles.emotionCard, { backgroundColor: emotion.color }]}
          >
            <Text style={styles.emotionText}>{emotion.name}</Text>
          </TouchableOpacity>
        ))}
      </View>
      <View style={{ flex: 1 }} />
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <TextInput
              style={styles.input}
              onChangeText={(text) => setInputText(text)}
              value={inputText}
              placeholder="Enter your message"
            />
            <Button onPress={handleSubmit} title="Submit" color="#2196F3" />
          </View>
        </View>
      </Modal>
      <SafeAreaView style={styles.container}>
        <Add user={auth.currentUser} />
        {snapshots ? <Messages messages={snapshots} /> : null}
      </SafeAreaView>
    </View>
  );
};

export default MoodScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },

  emotionCardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    width: '100%',
    height: deviceHeight * 0.8,
    alignItems: 'center',
  },

  emotionCard: {
    width: cardSize,
    height: cardSize,
    justifyContent: 'center',
    alignItems: 'center',
    margin: cardMargin,
    borderRadius: 10,
  },

  emotionText: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
  },
})
