import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { Card } from 'react-native-elements';
import { getDatabase, ref, onValue } from 'firebase/database';

const MuseumStoryCards = () => {
  const [user, authLoading, authError] = useAuthState(auth);
  const [stories, setStories] = React.useState([]);

  React.useEffect(() => {
    const database = getDatabase();
    const storyRef = ref(database, 'data');
    const onValueChange = onValue(storyRef, (snapshot) => {
      const data = snapshot.val();
      const formattedStories = formatStories(data);
      setStories(formattedStories);
    });

    return () => {
      storyRef.off('value', onValueChange);
    };
  }, []);

  const formatStories = (data) => {
    const formattedStories = [];
    if (!data) return formattedStories;

    const groupedStories = {};

    for (const key in data) {
      const story = data[key];
      if (!groupedStories[story.userId]) {
        groupedStories[story.userId] = {
          alias: '',
          location: '',
          time: '',
          recipient: '',
          story: '',
        };
      }
      switch (story.groupId) {
        case 30:
          groupedStories[story.userId].alias = story.string;
          break;
        case 31:
          groupedStories[story.userId].location = story.string;
          break;
        case 32:
          groupedStories[story.userId].time = story.string;
          break;
        case 33:
          groupedStories[story.userId].recipient = story.string;
          break;
        case 34:
          groupedStories[story.userId].story = story.string;
          break;
        default:
          break;
      }
    }

    for (const userId in groupedStories) {
      formattedStories.push(groupedStories[userId]);
    }

    return formattedStories;
  };

  return (
    <ScrollView>
      {stories.map((story, index) => (
        <Card key={index}>
          <Card.Title>{story.alias}</Card.Title>
          <Card.Divider />
          <View>
            <Text style={styles.text}>Location: {story.location}</Text>
            <Text style={styles.text}>Time: {story.time}</Text>
            <Text style={styles.text}>Recipient: {story.recipient}</Text>
            <Text style={styles.text}>Story: {story.story}</Text>
          </View>
        </Card>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  text: {
    marginBottom: 10,
  },
});

export default MuseumStoryCards;
